mapper.select:
SELECT id
FROM hotels
WHERE city = #{arrive_station}

select count(*)
from comments
where hotel_id = #{id}

select AVG(grade)
from comments
where hotel_id = #{id}

select MIN(num), MIN(price), name
from rooms
where date >= #{arrive_date}
  and date < #{ldeparture_date}
  and hotel_id = #{id}
group by name

select MIN(num), AVG(price), name,photo,size,others,bedSize
from rooms
where date >= #{arrive_date}
  and date < #{ldeparture_date}
  and hotel_id = #{id}
group by name,photo,size,others,bedSize

select photoUrl
from hotelphotos
where id=#{id}

select name, position
from hotels
where id = #{id}

mapper.update:
update rooms
set num=num-#{room_num}
where hotel_id=#{hotel_id}
  and date >= #{checkin_time}
  and date < #{checkout_time}

update rooms
set num=num+#{room_num}
where hotel_id=#{hotel_id}
  and date >= #{checkin_time}
  and date < #{checkout_time}
